import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/replitAuth.ts.content:
import * as client from "openid-client";
import { Strategy, type VerifyFunction } from "openid-client/passport";

import passport from "passport";
import session from "express-session";
import type { Express, RequestHandler } from "express";
import memoize from "memoizee";
import connectPg from "connect-pg-simple";
import { storage } from "./storage";

if (!process.env.REPLIT_DOMAINS) {
  throw new Error("Environment variable REPLIT_DOMAINS not provided");
}

const getOidcConfig = memoize(
  async () => {
    return await client.discovery(
      new URL(process.env.ISSUER_URL ?? "https://replit.com/oidc"),
      process.env.REPL_ID!
    );
  },
  { maxAge: 3600 * 1000 }
);

export function getSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const pgStore = connectPg(session);
  const sessionStore = new pgStore({
    conString: process.env.DATABASE_URL,
    createTableIfMissing: false,
    ttl: sessionTtl,
    tableName: "sessions",
  });
  return session({
    secret: process.env.SESSION_SECRET!,
    store: sessionStore,
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: true,
      maxAge: sessionTtl,
    },
  });
}

function updateUserSession(
  user: any,
  tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers
) {
  user.claims = tokens.claims();
  user.access_token = tokens.access_token;
  user.refresh_token = tokens.refresh_token;
  user.expires_at = user.claims?.exp;
}

async function upsertUser(
  claims: any,
) {
  await storage.upsertUser({
    id: claims["sub"],
    email: claims["email"],
    firstName: claims["first_name"],
    lastName: claims["last_name"],
    profileImageUrl: claims["profile_image_url"],
  });
}

export async function setupAuth(app: Express) {
  app.set("trust proxy", 1);
  app.use(getSession());
  app.use(passport.initialize());
  app.use(passport.session());

  const config = await getOidcConfig();

  const verify: VerifyFunction = async (
    tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers,
    verified: passport.AuthenticateCallback
  ) => {
    const user = {};
    updateUserSession(user, tokens);
    await upsertUser(tokens.claims());
    verified(null, user);
  };

  for (const domain of process.env
    .REPLIT_DOMAINS!.split(",")) {
    const strategy = new Strategy(
      {
        name: `replitauth:${domain}`,
        config,
        scope: "openid email profile offline_access",
        callbackURL: `https://${domain}/api/callback`,
      },
      verify,
    );
    passport.use(strategy);
  }

  passport.serializeUser((user: Express.User, cb) => cb(null, user));
  passport.deserializeUser((user: Express.User, cb) => cb(null, user));

  app.get("/api/login", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      prompt: "login consent",
      scope: ["openid", "email", "profile", "offline_access"],
    })(req, res, next);
  });

  app.get("/api/callback", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      successReturnToOrRedirect: "/",
      failureRedirect: "/api/login",
    })(req, res, next);
  });

  app.get("/api/logout", (req, res) => {
    req.logout(() => {
      res.redirect(
        client.buildEndSessionUrl(config, {
          client_id: process.env.REPL_ID!,
          post_logout_redirect_uri: `${req.protocol}://${req.hostname}`,
        }).href
      );
    });
  });
}

export const isAuthenticated: RequestHandler = async (req, res, next) => {
  const user = req.user as any;

  if (!req.isAuthenticated() || !user.expires_at) {
    return res.status(401).json({ message: "Unauthorized" });
  }

  const now = Math.floor(Date.now() / 1000);
  if (now <= user.expires_at) {
    return next();
  }

  const refreshToken = user.refresh_token;
  if (!refreshToken) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }

  try {
    const config = await getOidcConfig();
    const tokenResponse = await client.refreshTokenGrant(config, refreshToken);
    updateUserSession(user, tokenResponse);
    return next();
  } catch (error) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }
};

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { setupAuth, isAuthenticated } from "./replitAuth";
import { 
  insertAthleteProfileSchema, 
  insertTestResultSchema, 
  insertTestTypeSchema 
} from "@shared/schema";

export async function registerRoutes(app: Express): Promise<Server> {
  // Auth middleware
  await setupAuth(app);

  // Auth routes
  app.get("/api/auth/user", isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);
      const athleteProfile = await storage.getAthleteProfile(userId);

      res.json({
        ...user,
        athleteProfile,
      });
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  // Athlete profile routes
  app.post("/api/athlete-profile", isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const profileData = insertAthleteProfileSchema.parse({
        ...req.body,
        userId,
      });

      const profile = await storage.createOrUpdateAthleteProfile(profileData);
      res.json(profile);
    } catch (error) {
      console.error("Error creating/updating athlete profile:", error);
      res.status(400).json({ message: "Invalid profile data" });
    }
  });

  app.get("/api/athlete-profile", isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const profile = await storage.getAthleteProfile(userId);
      res.json(profile);
    } catch (error) {
      console.error("Error fetching athlete profile:", error);
      res.status(500).json({ message: "Failed to fetch profile" });
    }
  });

  // Test type routes
  app.get("/api/test-types", async (req, res) => {
    try {
      const testTypes = await storage.getAllTestTypes();
      res.json(testTypes);
    } catch (error) {
      console.error("Error fetching test types:", error);
      res.status(500).json({ message: "Failed to fetch test types" });
    }
  });

  app.post("/api/test-types", isAuthenticated, async (req, res) => {
    try {
      const testTypeData = insertTestTypeSchema.parse(req.body);
      const testType = await storage.createTestType(testTypeData);
      res.json(testType);
    } catch (error) {
      console.error("Error creating test type:", error);
      res.status(400).json({ message: "Invalid test type data" });
    }
  });

  // Test result routes
  app.post("/api/test-results", isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const athleteProfile = await storage.getAthleteProfile(userId);

      if (!athleteProfile) {
        return res.status(400).json({ message: "Athlete profile required" });
      }

      const resultData = insertTestResultSchema.parse({
        ...req.body,
        athleteId: athleteProfile.id,
      });

      const result = await storage.createTestResult(resultData);
      res.json(result);
    } catch (error) {
      console.error("Error creating test result:", error);
      res.status(400).json({ message: "Invalid test result data" });
    }
  });

  app.get("/api/test-results", isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const athleteProfile = await storage.getAthleteProfile(userId);

      if (!athleteProfile) {
        return res.json([]);
      }

      const results = await storage.getTestResultsByAthlete(athleteProfile.id);
      res.json(results);
    } catch (error) {
      console.error("Error fetching test results:", error);
      res.status(500).json({ message: "Failed to fetch test results" });
    }
  });

  app.get("/api/test-results/recent", isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const athleteProfile = await storage.getAthleteProfile(userId);

      if (!athleteProfile) {
        return res.json([]);
      }

      const limit = parseInt(req.query.limit as string) || 5;
      const results = await storage.getRecentTestResults(athleteProfile.id, limit);
      res.json(results);
    } catch (error) {
      console.error("Error fetching recent test results:", error);
      res.status(500).json({ message: "Failed to fetch recent test results" });
    }
  });

  // Leaderboard routes
  app.get("/api/leaderboards/state/:state", async (req, res) => {
    try {
      const { state } = req.params;
      const limit = parseInt(req.query.limit as string) || 50;
      const leaderboard = await storage.getStateLeaderboard(state, limit);
      res.json(leaderboard);
    } catch (error) {
      console.error("Error fetching state leaderboard:", error);
      res.status(500).json({ message: "Failed to fetch state leaderboard" });
    }
  });

  app.get("/api/leaderboards/national", async (req, res) => {
    try {
      const limit = parseInt(req.query.limit as string) || 100;
      const leaderboard = await storage.getNationalLeaderboard(limit);
      res.json(leaderboard);
    } catch (error) {
      console.error("Error fetching national leaderboard:", error);
      res.status(500).json({ message: "Failed to fetch national leaderboard" });
    }
  });

  // Achievement routes
  app.get("/api/achievements", async (req, res) => {
    try {
      const achievements = await storage.getAllAchievements();
      res.json(achievements);
    } catch (error) {
      console.error("Error fetching achievements:", error);
      res.status(500).json({ message: "Failed to fetch achievements" });
    }
  });

  app.get("/api/achievements/user", isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const athleteProfile = await storage.getAthleteProfile(userId);

      if (!athleteProfile) {
        return res.json([]);
      }

      const userAchievements = await storage.getUserAchievements(athleteProfile.id);
      res.json(userAchievements);
    } catch (error) {
      console.error("Error fetching user achievements:", error);
      res.status(500).json({ message: "Failed to fetch user achievements" });
    }
  });

  // Analytics routes
  app.get("/api/analytics", isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const athleteProfile = await storage.getAthleteProfile(userId);

      if (!athleteProfile) {
        return res.json({ totalTests: 0, performanceByCategory: {}, recentActivity: [] });
      }

      const analytics = await storage.getPerformanceAnalytics(athleteProfile.id);
      res.json(analytics);
    } catch (error) {
      console.error("Error fetching analytics:", error);
      res.status(500).json({ message: "Failed to fetch analytics" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import {
  users,
  athleteProfiles,
  testTypes,
  testResults,
  achievements,
  userAchievements,
  type User,
  type UpsertUser,
  type AthleteProfile,
  type InsertAthleteProfile,
  type TestType,
  type InsertTestType,
  type TestResult,
  type InsertTestResult,
  type Achievement,
  type UserAchievement,
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, sql, and, avg } from "drizzle-orm";

export interface IStorage {
  // User operations (required for Replit Auth)
  getUser(id: string): Promise<User | undefined>;
  upsertUser(user: UpsertUser): Promise<User>;

  // Athlete profile operations
  getAthleteProfile(userId: string): Promise<AthleteProfile | undefined>;
  createOrUpdateAthleteProfile(profile: InsertAthleteProfile): Promise<AthleteProfile>;

  // Test type operations
  getAllTestTypes(): Promise<TestType[]>;
  getTestType(id: string): Promise<TestType | undefined>;
  createTestType(testType: InsertTestType): Promise<TestType>;

  // Test result operations
  createTestResult(result: InsertTestResult): Promise<TestResult>;
  getTestResultsByAthlete(athleteId: string): Promise<TestResult[]>;
  getRecentTestResults(athleteId: string, limit?: number): Promise<any[]>;

  // Leaderboard operations
  getStateLeaderboard(state: string, limit?: number): Promise<any[]>;
  getNationalLeaderboard(limit?: number): Promise<any[]>;

  // Achievement operations
  getAllAchievements(): Promise<Achievement[]>;
  getUserAchievements(athleteId: string): Promise<any[]>;
  unlockAchievement(athleteId: string, achievementId: string): Promise<UserAchievement>;

  // Analytics operations
  getPerformanceAnalytics(athleteId: string): Promise<any>;
}

export class DatabaseStorage implements IStorage {
  // User operations
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(userData)
      .onConflictDoUpdate({
        target: users.id,
        set: {
          ...userData,
          updatedAt: new Date(),
        },
      })
      .returning();
    return user;
  }

  // Athlete profile operations
  async getAthleteProfile(userId: string): Promise<AthleteProfile | undefined> {
    const [profile] = await db
      .select()
      .from(athleteProfiles)
      .where(eq(athleteProfiles.userId, userId));
    return profile;
  }

  async createOrUpdateAthleteProfile(profile: InsertAthleteProfile): Promise<AthleteProfile> {
    const [existingProfile] = await db
      .select()
      .from(athleteProfiles)
      .where(eq(athleteProfiles.userId, profile.userId));

    if (existingProfile) {
      const [updatedProfile] = await db
        .update(athleteProfiles)
        .set({ ...profile, updatedAt: new Date() })
        .where(eq(athleteProfiles.id, existingProfile.id))
        .returning();
      return updatedProfile;
    } else {
      const [newProfile] = await db
        .insert(athleteProfiles)
        .values(profile)
        .returning();
      return newProfile;
    }
  }

  // Test type operations
  async getAllTestTypes(): Promise<TestType[]> {
    return await db
      .select()
      .from(testTypes)
      .where(eq(testTypes.isActive, true))
      .orderBy(testTypes.category, testTypes.name);
  }

  async getTestType(id: string): Promise<TestType | undefined> {
    const [testType] = await db
      .select()
      .from(testTypes)
      .where(eq(testTypes.id, id));
    return testType;
  }

  async createTestType(testType: InsertTestType): Promise<TestType> {
    const [newTestType] = await db
      .insert(testTypes)
      .values(testType)
      .returning();
    return newTestType;
  }

  // Test result operations
  async createTestResult(result: InsertTestResult): Promise<TestResult> {
    const [newResult] = await db
      .insert(testResults)
      .values(result)
      .returning();

    // Update athlete's overall score
    await this.updateAthleteOverallScore(result.athleteId);

    return newResult;
  }

  async getTestResultsByAthlete(athleteId: string): Promise<TestResult[]> {
    return await db
      .select()
      .from(testResults)
      .where(eq(testResults.athleteId, athleteId))
      .orderBy(desc(testResults.createdAt));
  }

  async getRecentTestResults(athleteId: string, limit: number = 5): Promise<any[]> {
    return await db
      .select({
        id: testResults.id,
        value: testResults.value,
        createdAt: testResults.createdAt,
        testName: testTypes.name,
        testUnit: testTypes.unit,
        testCategory: testTypes.category,
      })
      .from(testResults)
      .innerJoin(testTypes, eq(testResults.testTypeId, testTypes.id))
      .where(eq(testResults.athleteId, athleteId))
      .orderBy(desc(testResults.createdAt))
      .limit(limit);
  }

  // Leaderboard operations
  async getStateLeaderboard(state: string, limit: number = 50): Promise<any[]> {
    return await db
      .select({
        rank: sql<number>`ROW_NUMBER() OVER (ORDER BY ${athleteProfiles.overallScore} DESC)`,
        athleteId: athleteProfiles.id,
        firstName: users.firstName,
        lastName: users.lastName,
        score: athleteProfiles.overallScore,
        sport: athleteProfiles.sport,
        city: athleteProfiles.city,
      })
      .from(athleteProfiles)
      .innerJoin(users, eq(athleteProfiles.userId, users.id))
      .where(eq(athleteProfiles.state, state))
      .orderBy(desc(athleteProfiles.overallScore))
      .limit(limit);
  }

  async getNationalLeaderboard(limit: number = 100): Promise<any[]> {
    return await db
      .select({
        rank: sql<number>`ROW_NUMBER() OVER (ORDER BY ${athleteProfiles.overallScore} DESC)`,
        athleteId: athleteProfiles.id,
        firstName: users.firstName,
        lastName: users.lastName,
        score: athleteProfiles.overallScore,
        sport: athleteProfiles.sport,
        state: athleteProfiles.state,
        city: athleteProfiles.city,
      })
      .from(athleteProfiles)
      .innerJoin(users, eq(athleteProfiles.userId, users.id))
      .orderBy(desc(athleteProfiles.overallScore))
      .limit(limit);
  }

  // Achievement operations
  async getAllAchievements(): Promise<Achievement[]> {
    return await db
      .select()
      .from(achievements)
      .where(eq(achievements.isActive, true))
      .orderBy(achievements.category, achievements.name);
  }

  async getUserAchievements(athleteId: string): Promise<any[]> {
    return await db
      .select({
        id: userAchievements.id,
        unlockedAt: userAchievements.unlockedAt,
        name: achievements.name,
        description: achievements.description,
        icon: achievements.icon,
        category: achievements.category,
        points: achievements.points,
      })
      .from(userAchievements)
      .innerJoin(achievements, eq(userAchievements.achievementId, achievements.id))
      .where(eq(userAchievements.athleteId, athleteId))
      .orderBy(desc(userAchievements.unlockedAt));
  }

  async unlockAchievement(athleteId: string, achievementId: string): Promise<UserAchievement> {
    const [newUserAchievement] = await db
      .insert(userAchievements)
      .values({ athleteId, achievementId })
      .returning();
    return newUserAchievement;
  }

  // Analytics operations
  async getPerformanceAnalytics(athleteId: string): Promise<any> {
    const testResultsData = await db
      .select({
        testName: testTypes.name,
        testCategory: testTypes.category,
        value: testResults.value,
        createdAt: testResults.createdAt,
      })
      .from(testResults)
      .innerJoin(testTypes, eq(testResults.testTypeId, testTypes.id))
      .where(eq(testResults.athleteId, athleteId))
      .orderBy(testResults.createdAt);

    // Calculate performance trends
    const performanceByCategory = testResultsData.reduce((acc, result) => {
      if (!acc[result.testCategory]) {
        acc[result.testCategory] = [];
      }
      acc[result.testCategory].push({
        value: parseFloat(result.value),
        date: result.createdAt,
      });
      return acc;
    }, {} as Record<string, Array<{ value: number; date: Date }>>);

    return {
      totalTests: testResultsData.length,
      performanceByCategory,
      recentActivity: testResultsData.slice(-10),
    };
  }

  // Helper method to update athlete's overall score
  private async updateAthleteOverallScore(athleteId: string): Promise<void> {
    const avgScore = await db
      .select({
        avgScore: avg(testResults.value),
      })
      .from(testResults)
      .where(eq(testResults.athleteId, athleteId));

    if (avgScore[0]?.avgScore) {
      await db
        .update(athleteProfiles)
        .set({
          overallScore: avgScore[0].avgScore,
          updatedAt: new Date(),
        })
        .where(eq(athleteProfiles.id, athleteId));
    }
  }
}

export const storage = new DatabaseStorage();

